import React, { useState, useEffect } from "react";
import "../styles.css";
import MovieCard from "./MovieCard";

export function MovieGrid() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const [genre, setGenre] = useState("All Genres");
  const [rating, setRating] = useState("All");
  useEffect(() => {
    fetch("movies.json")
      .then((response) => response.json())
      .then((data) => setMovies(data));
  }, []);
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };
  const handleGenre = (e) => {
    setGenre(e.target.value);
  };
 const matchesGenre=(movies,genre) => {
    return genre === "All Genres" || movies.genre.toLowerCase()===genre.toLowerCase();
 }
const matchSearchTerm =(movies, searchTerm) =>{
    movies.title.toLowerCase().includes(searchTerm.toLowerCase())
}
  const filteredMovies = movies.filter((movies) => { 
 matchesGenre(movies,genre) && matchSearchTerm(movies,searchTerm);
});

  return (
    <div>
      <input
        type="text"
        className="search-input"
        placeholder="Search movies..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <div className="filter-bar">
        <div className="filter-slot">
          <label>Genres</label>
          <select className="filter-dropdown"
          value={genre}
          onChange={handleGenre}>
            <option>All Genres</option>
            <option>Action</option>
            <option>Drama</option>
            <option>Fantacy</option>
            <option>Horror</option>
          </select>
        </div>
        <div className="filter-slot">
        <label>Rating</label>
        <select className="filter-dropdown">
            <option>All</option>
            <option>Good</option>
            <option>Ok</option>
            <option>Bad</option>
          </select>
        </div>
      </div>
      <div className="movies-grid">
        {filteredMovies.map((movie) => (
          <MovieCard movie={movie}></MovieCard>
        ))}
      </div>
    </div>
  );
}
